import requests
from datetime import datetime

def getPromoCode(depDateObject):
    promoCodes = [
        {
            'code': 'VALIGIA', 'expires': '19-09-22 18', 'validAfter': '24-09-22',
            'code': 'COLORI', 'expires': '17-10-22 18', 'validAfter': '18-10-22'
         }
    ]
    promoCode = ''
    curDateObject = datetime.now()
    for code in promoCodes:
        expireDateObject = datetime.strptime(code['expires'], '%d-%m-%y %H')
        if expireDateObject > curDateObject:
            if datetime.strptime(code['validAfter'], '%d-%m-%y') < depDateObject:
                promoCode = code['code']
    return promoCode

